const smilyLogo='https://images.app.goo.gl/gTWR52Qgx5sQcyzX7';
function NavBar() {
 const [show, handleShow]=useState();
    useEffect(()=>{
        window.addEventListener('scroll',()=>{
           window.scrollY>150?handleShow(true):handleShow(false)
        });
