        }
    })
    return (
        <>
           <div className={`NavBar ${show?"NavBar_bg":null}`}>
               <img className='NavBar_logo1'  src={netflixLogo} alt="netflixLogo"/>
               <img className='NavBar_logo2' src={Smilylogo} alt="smilyLogo"/>
           </div>
        </>
    )
}

