    useEffect(() => {
        const func = async () => {
            const response = await axios.get(`${movies_requests.fetchNetflixOriginals}`)
            const advanceRes = response.data.results[Math.round(Math.random() * response.data.results.length)];
            upMovie(advanceRes);
        }
        func();
    }, [])

