import {React,useEffect, useState} from 'react'
import axios from 'axios';
import YouTube from 'react-youtube';

const baseurl="https://image.tmdb.org/t/p/original";

function Row({title, url, isLargerRow}) {
    // var jsonRes;
    const [movie, upMovie]=useState([]);
    useEffect(() => {
        const func = async() => {
            // const api_Key=`b1bcd518764c9ced91d1c21adad8eb44`;
            const res = await axios.get(`${url}`)
            // const jsonRes = res.json();
            // console.log(res);
            upMovie(res.data.results)
            // return jsonRes;
          }
          func(); 
    }, [url])

    const opts = {
      height: '390',
      width: '640',
      playerVars: {
        // https://developers.google.com/youtube/player_parameters
        autoplay: 1,
      },
    }
    
    
    return (
        <>
        <div className='row_main'>
           <h2 className='row_heading'>{title}</h2>
           <div className="row_inner">
           {movie.map((val)=>{
               return (<img onClick={console.log('uff')} key={val.id} className={isLargerRow ? 'poster_img': 'poster_path'} src={`${baseurl}${isLargerRow? val.poster_path : val.backdrop_path}`} alt={val.original_title}/>);
           })}
               
           </div> 
        </div>
    <div>
       <YouTube videoId='2g811Eo7K8U' opts={opts}/>
    </div>
    </>
    )
}

export default Row;
// https://image.tmdb.org/t/p/orignal/pci1ArYW7oJ2eyTo2NMYEKHHiCP.jpg
